import React from "react";

class AddContact extends React.Component {
	state = {
		nombre: "",
		email: "", 
		contrasenha: "", 
		contrasenhaconf: "", 
	}
	
	add = (e) => {
		e.preventDefault();
		if(this.state.contrasenha ==="" || this.state.email === "") {
			alert("campos obligatorios");
			return
		}
		this.props.addContactHandler(this.state);
		this.setState({ contrasenha: "", email: ""});
		console.log(this.state);
		this.props.history.push("/Bienvenido");
	}
	
	render(){
		return(
		<div className="ui main">
		
		<h3 style={{color:"red", marginTop:"7px"}} >Registro</h3>
		
		<div class="form-group">
			<label for="exampleInputEmail1">Introduce tu dirección de correo electrónico</label>
			
    
		</div>
  
			<form className="ui form" onSubmit={this.add}>
				 
				<div className="field">
					<input style ={{border: "2px", background: "transparent"}}
						type="text"
						name="email"
						placeholder="Email"  value={this.state.email} onChange={( e )=> this.setState({email: e.target.value})} />
				</div>
				<div className="field">
						<input
							type="pass"
							name="name"
							placeholder="Contraseña" value={this.state.contrasenha} onChange={( e )=> this.setState({contrasenha: e.target.value})}/>
				</div>
				<div className="field">
						<input
							type="pass"
							name="name"
							placeholder="Contraseña" value={this.state.contrasenhaconf} onChange={( e )=> this.setState({contrasenhaconf: e.target.value})}/>
				</div>
				<button className="ui button blue">Continuar</button>
			</form>
			
			<div className="container">
				<form className="text-center">
				<h3 style=" color: #ED2E5D; margin-top : 50px; margin-bottom:50px">Registro</h3>
				<div className="form-group">
					<label for="exampleInputEmail1">Introduce tu dirección de correo electrónico</label>
					<input type="pass" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"placeholder="hola@conectamama.cl"/>
				</div>
			</form>
			</div>
		</div>
		  
		
		);
	}
	
}

export default AddContact;
