import React from "react";

class AddContact extends React.Component {
	state = {
		nombre: "",
		email: "", 
	}
	
	add = (e) => {
		e.preventDefault();
		if(this.state.nombre ==="" || this.state.email === "") {
			alert("campos obligatorios");
			return
		}
		this.props.addContactHandler(this.state);
		this.setState({ nombre: "", email: ""});
		console.log(this.state);
		this.props.history.push("/lista");
	}
	
	render(){
		return(
		<div className="ui main">
		
		<h3 style={{color:"red", marginTop:"7px"}} >Registro</h3>
		
		<div class="form-group">
			<label for="exampleInputEmail1">Introduce tu direcci칩n de correo electr칩nico</label>
			
    
		</div>
  
			<form className="ui form" onSubmit={this.add}>
				 
				<div className="field">
					<input
						type="text"
						name="email"
						placeholder="Email"  value={this.state.email} onChange={( e )=> this.setState({email: e.target.value})} />
				</div>
				<div className="field">
						<input
							type="pass"
							name="name"
							placeholder="Contrase침a" value={this.state.nombre} onChange={( e )=> this.setState({nombre: e.target.value})}/>
				</div>
				<div className="field">
						<input
							type="pass"
							name="name"
							placeholder="Contrase침a" value={this.state.nombre} onChange={( e )=> this.setState({nombre: e.target.value})}/>
				</div>
				<button className="ui button blue">Agregar</button>
			</form>
		</div>
		  
		
		);
	}
	
}

export default AddContact;
